card.tex:
\begin{aligned}

\text{Si }a\text{ est extrémité de }I\text{ hors de }I

,\ \lim_{x\to a}f(x)=\ell
\ \Longleftrightarrow\ 
\lim_{x\to a^-}f(x)=\lim_{x\to a^+}f(x)=f(a)=\ell\in \overline{\mathbb{R}} \\ \text{Si }a\in I
,\ 
\lim_{x\to a}f(x)=\ell
\ \Longleftrightarrow\ 
\lim_{x\to a^-}f(x)=\lim_{x\to a^+}f(x)=\ell \in \mathbb{R}
\end{aligned}

\lim_{x\to a}f(x)=\ell\in \overline{\mathbb{R}}
\ \iff\ 
\forall u\in I^\mathbb{N}, u_n\to_{n\to +\infin} a\ \Rightarrow\ f(u_n)\to_{n\to + \infin} \ell.

\begin{aligned}
Droite : \lim_{x\to a^+}f(x)=\ell\quad\Longleftrightarrow\quad f|_{I\cap]a,+\infty[}\xrightarrow[x\to a]{}\ell,\\
Gauche : \lim_{x\to a^-}f(x)=\ell\quad\Longleftrightarrow\quad f|_{I\cap]-\infty,a[}\xrightarrow[x\to a]{}\ell.
\end{aligned}
